mapper.select:
SELECT COUNT(*) FROM kaaudit ka


		ka.auditoid = #{auditOid}



		ka.EVENT_CODE = #{eventCode}



		ka.userId like '%${s_value}%'


order by ${orderBy}

SELECT * FROM (
SELECT A.*,
ROWNUM AS RNUM,
FLOOR((ROWNUM-1)/${pageSize}+1) AS PAGE,
COUNT(*) OVER() AS TOTCNT FROM (
SELECT

FROM kaaudit ka


		ka.auditoid = #{auditOid}



		ka.EVENT_CODE = #{eventCode}



		ka.userId like '%${s_value}%'


order by ${orderBy}
) A
) WHERE 1 = 1

	AND PAGE = ${pageNo}
